class DynamicText{constructor(elementClassOrID,durationInSeconds,ArrayOfTexts){this.elementSelector=elementClassOrID;this.duration=durationInSeconds;this.texts=ArrayOfTexts;this.begin()}begin(){let element;if(this.elementSelector.startsWith("#"))element=document.querySelector(this.elementSelector);else element=document.querySelector(this.elementSelector);if(!element)return console.error(`${this.elementSelector} not exist! Please provide an actual valid element.`);let i=0;const update=()=>{element.textContent=this.texts[i];element.style.animation="none";element.offsetHeight;element.style.whiteSpace="nowrap";element.style.overflow="hidden";element.style.borderRight="3px solid black";element.style.width="0";element.style.animation=`typing ${this.duration}s steps(${this.texts[i].length}, end) 1, blink-caret 0.75s step-end infinite`;setTimeout((()=>{element.style.animation="none";element.style.width="0";element.offsetHeight;setTimeout((()=>{element.style.animation=`typing ${this.duration}s steps(${this.texts[i].length}, end) 1, blink-caret 0.75s step-end infinite`;i=(i+1)%this.texts.length;update()}),500)}),this.duration*1e3+500)};update()}}const Test1=new DynamicText("#motivational_sentence",12,["Cada logro comienza con la decisión de intentarlo","La clave del éxito es empezar antes de estar listo","El éxito es la suma de pequeños esfuerzos diarios","Si puedes soñarlo, puedes lograrlo.","El único límite es el que te pones a ti mismo","No te rindas. Los grandes logros toman tiempo.","No dejes que el miedo te detenga.","El único fracaso real es no intentarlo.","Haz que cada día cuente para tus sueños.","Compárate solo con tu mejor versión, no con otros.","El esfuerzo de hoy es el éxito de mañana.","Cada desafío es una oportunidad para crecer.","Si caes, levántate más fuerte y sigue adelante.","Tu futuro comienza hoy; construye con tu esfuerzo."]);