class ScrollObserver{constructor(selector,className,threshold=.1){this.selector=selector;this.className=className;this.threshold=threshold;this.init()}init(){const element=document.querySelector(this.selector);if(!element){console.error(`Element with selector "${this.selector}" not found.`);return}const observer=new IntersectionObserver((entries=>{entries.forEach((entry=>{if(entry.isIntersecting){entry.target.classList.add(this.className)}}))}),{threshold:this.threshold});observer.observe(element)}}new ScrollObserver(".skills","exposed",.2);new ScrollObserver(".projects","exposed",.2);new ScrollObserver(".contact","exposed",.2);