class GalleryViewer{constructor(galleryId,imagesArr){this.id=galleryId;this.images=imagesArr;this.init()}init(){document.addEventListener("DOMContentLoaded",(()=>this.setupGallery()))}setupGallery(){const openBtns=document.querySelectorAll(`.open-g[g-id="${this.id}"]`),modal=document.querySelector(".g-modal"),content=modal.querySelector(".g-content");var i=0;openBtns.forEach((btn=>{btn.addEventListener("click",(()=>{if(this.images.length>0){this.showImages(content,modal,i)}else{this.showMessage(content,modal,Object.assign(document.createElement("div"),{className:"g-message",innerText:"No hay imágenes"}))}}))}))}showImages(content,modal,index){content.innerHTML=`\n      <div class="g-img-container">\n        <button class="g-close">&times;</button>\n        <button class="g-prev">&#10094;</button>\n        <img class="g-image" src="${this.images[index]}"/>\n        <button class="g-next">&#10095;</button>\n      </div>\n    `;if(this.images.length<=1)content.innerHTML=`\n    <div class="g-img-container">\n      <button class="g-close">&times;</button>\n      <img class="g-image" src="${this.images[index]}"/>\n    </div>\n    `;this.setupGallery();modal.classList.add("active");const closeBtn=document.querySelector(".g-close"),prevBtn=document.querySelector(".g-prev"),nextBtn=document.querySelector(".g-next");closeBtn.addEventListener("click",(()=>{this.closeGallery(content,modal)}));if(this.images.length>1){prevBtn.addEventListener("click",(()=>{if(this.images.length>0){index=(index-1+this.images.length)%this.images.length;content.querySelector(".g-image").src=this.images[index]}}));nextBtn.addEventListener("click",(()=>{if(this.images.length>0){index=(index+1)%this.images.length;content.querySelector(".g-image").src=this.images[index]}}))}}showMessage(content,modal,message){content.innerHTML="";content.appendChild(message);modal.classList.add("active")}closeGallery(content,modal){modal.classList.remove("active");content.innerHTML=""}}const GallerySetups=[{id:"kazuto_music_project",images:["assets/projects/kazuto_music/play-preview.webp","assets/projects/kazuto_music/play-preview2.webp","assets/projects/kazuto_music/queue-preview.webp","assets/projects/kazuto_music/commands-preview.webp","assets/projects/kazuto_music/playlist-preview1.webp","assets/projects/kazuto_music/playlist-preview2.webp","assets/projects/kazuto_music/playlist-preview3.webp","assets/projects/kazuto_music/settings-preview.webp","assets/projects/kazuto_music/lang-preview.webp"]}];GallerySetups.forEach((p=>new GalleryViewer(p.id,p.images)));document.querySelectorAll(".open-g").forEach((btn=>{if(!GallerySetups.find((g=>g.id==btn.getAttribute("g-id")))){btn.addEventListener("click",(()=>{const modal=document.querySelector(".g-modal");const content=modal.querySelector(".g-content");content.innerHTML="";const closeBtn=Object.assign(document.createElement("button"),{className:"g-nclose",innerHTML:"&times;"});closeBtn.addEventListener("click",(()=>{modal.classList.remove("active");content.innerHTML=""}));const message=Object.assign(document.createElement("div"),{className:"g-message",innerText:"No se encontraron imágenes"});[closeBtn,message].forEach((e=>content.appendChild(e)));modal.classList.add("active")}))}}));