class ScrollDetect{constructor(initialClass,classToAdd,logic,X=0,Y=0){this.initialClass=document.querySelector(initialClass);this.classToAdd=classToAdd;this.logic=logic;this.X=X;this.Y=Y;this.init()}init(){window.addEventListener("scroll",(()=>this.checkScroll()));this.checkScroll()}checkScroll(){switch(this.logic){case"greater":{if(this.validateAndCheckScroll(this.X,window.scrollX,"greater")||this.validateAndCheckScroll(this.Y,window.scrollY,"greater")){this.initialClass.classList.add(this.classToAdd)}else{this.initialClass.classList.remove(this.classToAdd)}}break;case"lesser":{if(this.validateAndCheckScroll(this.X,window.scrollX,"lesser")||this.validateAndCheckScroll(this.Y,window.scrollY,"lesser")){this.initialClass.classList.add(this.classToAdd)}else{this.initialClass.classList.remove(this.classToAdd)}}break;default:console.error("Invalid logic!")}}validateAndCheckScroll(threshold,scrollValue,logic){if(isNaN(threshold))return false;if(logic==="greater")return scrollValue>threshold;if(logic==="lesser")return scrollValue<threshold}}new ScrollDetect(".navbar-list","shrink-nav","greater",0,20);new ScrollDetect(".toggle-navbar","active","greater",0,20);