class SuccessVisualConfirm{constructor(button,text){this.button=button;this.text=text;this.init()}init(){const buttons=document.querySelectorAll(`.${this.button}`);buttons.forEach((button=>{button.addEventListener("click",(()=>{if(button.className==="copy-email"){var text=document.querySelector(`.${this.text}`).value;var tooltip=document.querySelector(".tooltip-copy");document.styleSheets[0].insertRule(`\n            .tooltip-copy.hidden {\n              visibility: hidden !important;\n              opacity: 0 !important;\n            }\n          `,0);navigator.clipboard.writeText(text).then((()=>{button.classList.add("success");tooltip.classList.add("hidden");setTimeout((()=>{button.classList.remove("success");tooltip.classList.remove("hidden")}),2e3)}))}else if(button.className==="send-email"){var tooltip=document.querySelector(".tooltip-send");document.styleSheets[0].insertRule(`\n            .tooltip-send.hidden {\n              visibility: hidden !important;\n              opacity: 0 !important;\n            }\n          `,0);button.classList.add("success");button.classList.add("disabled");tooltip.classList.add("hidden");setTimeout((()=>{button.classList.remove("success");button.classList.remove("disabled");tooltip.classList.remove("hidden")}),2e3)}}))}))}}new SuccessVisualConfirm("copy-email","email");new SuccessVisualConfirm("send-email","email");